eventApp.controller('MainCtrl', function ($scope) {
    $scope.items = [
        {"name": "Filter"},
        {"name": "To Do List"}]
    $scope.selectedIndex = 0;
    $scope.active = function($index) {
        $scope.selectedIndex = $index;

    }

});


eventApp.controller('ToDoCtrl', function ($scope, $filter) {
$scope.list = { items: items }
$scope.all=function() {
$scope.list.items=$scope.list.items.map(e=>{return {...e,done:true}})
}

$scope.none=function() {
$scope.list.items=$scope.list.items.map(e=>{return {...e,done:false}})
}


    $scope.add = function(name){
console.dir($scope);
i=[];
$scope.list.items.filter(e=>e.done).forEach(e=>{
i.push(e.name);
});
i=i.join(`\n`);
download('text.txt',i)
    }

    $scope.$watch('itemscheked',function(){
        countchecked()
    })

    $scope.checked = function(done){
        if(done){
            $scope.complitedCount =-1;
            return true;
        }else{
            $scope.complitedCount =+1;
            return false;
        }
    }

    function countchecked(){
        $scope.complitedCount = $filter('filter')($scope.list.items, { done: true }).length;
    }

    $scope.delete = function(index){
        $scope.list.items.splice(index,1)
    }

    $scope.clearAll = function () {
        //$filter('filter')(todos, { completed: false }).length
        $scope.list.items = $filter('filter')($scope.list.items, { done: false });

//            list.items.$filter(function (val) {
//            return !val.done;
//        });
    }

});

